import React, { useState } from 'react';
import axios from 'axios';

const AddFeaturedProjectForm: React.FC = () => {
  const [form, setForm] = useState({
    title: '',
    description: '',
    shortDescription: '',
    category: '',
    domain: '',
    subDomain: '',
    controller: '',
    difficulty: 'Intermediate',
    price: '',
    originalPrice: '',
    discount: '',
    file: null,
  });
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value, files } = e.target as any;
    setForm((prev) => ({
      ...prev,
      [name]: files ? files[0] : value,
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');
    try {
      const formData = new FormData();
      Object.entries(form).forEach(([key, value]) => {
        if (value) formData.append(key, value);
      });
      const res = await axios.post('/api/admin/projects/featured', formData);
      setSuccess('Featured project added successfully!');
      setForm({
        title: '', description: '', shortDescription: '', category: '', domain: '', subDomain: '', controller: '', difficulty: 'Intermediate', price: '', originalPrice: '', discount: '', file: null,
      });
    } catch (err: any) {
      setError(err.response?.data?.error || 'Failed to add project');
    }
    setLoading(false);
  };

  return (
    <form className="bg-white p-6 rounded-lg shadow-md max-w-xl mx-auto" onSubmit={handleSubmit} encType="multipart/form-data">
      <h2 className="text-2xl font-bold mb-4">Add Featured Project</h2>
      {success && <div className="text-green-600 mb-2">{success}</div>}
      {error && <div className="text-red-600 mb-2">{error}</div>}
      <input name="title" value={form.title} onChange={handleChange} placeholder="Title" className="input mb-2 w-full" required />
      <textarea name="description" value={form.description} onChange={handleChange} placeholder="Description" className="input mb-2 w-full" required />
      <input name="shortDescription" value={form.shortDescription} onChange={handleChange} placeholder="Short Description" className="input mb-2 w-full" />
      <input name="category" value={form.category} onChange={handleChange} placeholder="Category ID" className="input mb-2 w-full" required />
      <input name="domain" value={form.domain} onChange={handleChange} placeholder="Domain" className="input mb-2 w-full" required />
      <input name="subDomain" value={form.subDomain} onChange={handleChange} placeholder="Sub Domain" className="input mb-2 w-full" />
      <input name="controller" value={form.controller} onChange={handleChange} placeholder="Controller" className="input mb-2 w-full" />
      <select name="difficulty" value={form.difficulty} onChange={handleChange} className="input mb-2 w-full">
        <option value="Beginner">Beginner</option>
        <option value="Intermediate">Intermediate</option>
        <option value="Advanced">Advanced</option>
        <option value="Expert">Expert</option>
      </select>
      <input name="price" value={form.price} onChange={handleChange} placeholder="Price" type="number" className="input mb-2 w-full" required />
      <input name="originalPrice" value={form.originalPrice} onChange={handleChange} placeholder="Original Price" type="number" className="input mb-2 w-full" />
      <input name="discount" value={form.discount} onChange={handleChange} placeholder="Discount" type="number" className="input mb-2 w-full" />
      <input name="file" type="file" onChange={handleChange} className="input mb-2 w-full" />
      <button type="submit" className="btn btn-primary w-full" disabled={loading}>{loading ? 'Adding...' : 'Add Featured Project'}</button>
    </form>
  );
};

export default AddFeaturedProjectForm;
